mapper.select:
SELECT ap.pk, ap.Date AS date, ap.ProductId AS productId, p.name AS productName, p.price AS price, ap.Amount AS amount FROM assetplan ap LEFT JOIN product p ON ap.ProductId = p.pk
AND ap.Date = #{planDate}
AND p.name LIKE CONCAT('%', #{productName}, '%')
AND ap.Amount = #{salesQty}
ORDER BY ap.Date ASC
