mapper.update:
UPDATE BOM
SET MaterialAmount = #{materialAmount}
WHERE ProductId = #{productId} AND MaterialId = #{materialId}

mapper.select:
SELECT * FROM BOM




                AND ProductId = #{id}


                AND MaterialId = #{id}




        AND (ProductId = #{id} OR MaterialId = #{id})

SELECT b.ProductId, p.Name AS productName, b.MaterialId, m.Name AS materialName, b.MaterialAmount FROM BOM b JOIN product p ON b.ProductId = p.pk JOIN material m ON b.MaterialId = m.pk
AND b.ProductId = #{id}
AND b.MaterialId = #{id}
AND (b.ProductId = #{id} OR b.MaterialId = #{id})
