mapper.update:
UPDATE mps
SET ProductId = #{productId},
    Period = #{period},
    Volume = #{volume}
WHERE pk = #{pk}

mapper.select:
SELECT mps.*, p.name AS productName, p.price AS price FROM MPS mps JOIN Product p ON mps.ProductId = p.pk
AND p.pk = #{prodCode}
AND p.name LIKE CONCAT('%', #{prodName}, '%')
