mapper.select:
SELECT mrp.pk, p.Name AS ProductName, m.Name AS MaterialName, m.Stock AS StockQuantity, mrp.Requirement AS RequiredQuantity, mrp.Date AS ProductionPlan, (mrp.Requirement - m.Stock) AS Shortage, mrp.MRPStatus FROM mrp LEFT JOIN product p ON mrp.ProductId = p.pk LEFT JOIN material m ON mrp.MaterialId = m.pk
AND p.pk = #{prodCode}
AND p.Name LIKE CONCAT('%', #{prodName}, '%')
AND mrp.Date = #{inDate}
AND mrp.MRPStatus = #{mrpStatus}
