mapper.select:
SELECT p.pk AS ProductId, t.Date, p.Name AS ProductName, t.Sales AS Amount, p.Price, (t.Sales * p.Price) AS Total, t.Earning, p.Stock FROM transaction_details t LEFT JOIN product p ON t.ProductId = p.pk
AND p.pk = #{prodCode}
AND p.Name LIKE CONCAT('%', #{prodName}, '%')
AND t.QC = #{qc}
AND t.Date >= #{startDate}
AND t.Date <= #{endDate}
ORDER BY t.Date DESC
