mapper.select:
SELECT o.pk AS orderNo, o.Date AS orderDate, o.ProductId AS prodCode, o.ProductName AS prodName, o.Amount AS qty, o.Price AS unitPrice, o.Total AS amount, o.OrderStatus AS status FROM `order` o
AND o.ProductId = #{prodCode}
AND o.ProductName LIKE CONCAT('%', #{prodName}, '%')
AND o.Date = #{orderDate}
AND o.OrderStatus = #{status}
ORDER BY o.Date DESC, o.pk DESC
