mapper.select:
SELECT td.pk AS pk, td.Date AS saleDate, td.ProductId AS productId, p.Name AS productName, td.Sales AS quantity, td.Earning AS earning, p.price AS price, p.category AS category FROM `transaction_details` td INNER JOIN product p ON td.ProductId = p.pk
AND td.ProductId LIKE CONCAT('%', #{code}, '%')
AND p.Name LIKE CONCAT('%', #{name}, '%')
AND td.Date = #{outDate}
AND p.category = #{category}
ORDER BY td.Date DESC
